INSERT INTO `traloicodongs` (`id`, `user_id`, `ykiencodong_id`, `reply_content`, `author`, `deleted_at`, `created_at`, `updated_at`) VALUES
(1, 1, 1, '<p style=\"text-align:justify\"><span style=\"font-size:9pt\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"font-size:12px\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"background-color:#ffffff\"><span style=\"font-size:large\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"font-size:9pt\"><span style=\"font-family:Arial\"><span style=\"font-size:9pt\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\">K&iacute;nh gửi Cổ đ&ocirc;ng L&ecirc; Thị Phương</span></span></span></span></span></span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:9pt\"><span style=\"background-color:white\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"font-size:12px\"><span style=\"font-size:large\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"font-size:9pt\"><span style=\"font-family:Arial\"><span style=\"font-size:9pt\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\">Lời đầu ti&ecirc;n, C&ocirc;ng ty Cổ phần S&ocirc;ng Ba (SBA) ch&acirc;n th&agrave;nh cảm ơn Qu&yacute; Cổ đ&ocirc;ng đ&atilde; quan t&acirc;m đến t&igrave;nh h&igrave;nh hoạt động của SBA. C&acirc;u hỏi của Qu&yacute; Cổ đ&ocirc;ng, SBA trả lời như sau:</span></span></span></span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:9pt\"><span style=\"background-color:white\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\">&nbsp;</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:9pt\"><span style=\"background-color:white\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"font-size:12px\"><span style=\"font-size:9pt\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\">Ng&agrave;y 20/3/2020 Ủy ban Chứng kho&aacute;n Nh&agrave; nước đ&atilde; ph&aacute;t h&agrave;nh văn bản số 1916/UBCK-GSĐC, V/v Tổ chức Đại hội đồng cổ đ&ocirc;ng thường ni&ecirc;n năm 2020, hướng dẫn tổ chức ĐHĐCĐ theo h&igrave;nh thức trực tuyến. Theo đ&oacute;, để đủ điều kiện họp ĐHĐCĐ theo h&igrave;nh thức trực tuyến, SBA cần phải bổ sung nội dung quy định việc &aacute;p dụng c&ocirc;ng nghệ th&ocirc;ng tin hiện đại để cổ đ&ocirc;ng c&oacute; thể tham dự v&agrave; ph&aacute;t biểu &yacute; kiến tại cuộc họp ĐHĐCĐ v&agrave;o Quy chế nội bộ về quản trị của C&ocirc;ng ty cổ phần S&ocirc;ng Ba v&agrave; xin &yacute; kiến ĐHĐCĐ th&ocirc;ng qua.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:start\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"background-color:white\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\">&nbsp;</span></span></span></span></p>\r\n\r\n<p style=\"text-align:start\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"background-color:white\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"font-size:9pt\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\">Để ĐHĐCĐ theo h&igrave;nh thức trực tuyến được tổ chức đảm bảo t&iacute;nh hợp lệ, chu đ&aacute;o, kh&ocirc;ng bị gi&aacute;n đoạn v&agrave; đạt hiệu quả cao, SBA cần phải thu&ecirc; đơn vị Tư vấn v&agrave; đầu tư trang thiết bị tổ chức Hội nghị chuy&ecirc;n nghiệp nhằm&nbsp;cung cấp nền tảng ứng dụng, h&igrave;nh ảnh, &acirc;m thanh tốt nhất&nbsp;<span style=\"font-size:9pt\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\">đến cổ đ&ocirc;ng tham dự trực tuyến. Điều n&agrave;y đ&ograve;i hỏi SBA phải đầu tư chi ph&iacute; rất lớn. FPT l&agrave; tập đo&agrave;n c&oacute; tiềm lực rất mạnh về t&agrave;i ch&iacute;nh v&agrave; c&ocirc;ng nghệ, SBA đang cố gắng vươn l&ecirc;n, kh&ocirc;ng thể so s&aacute;nh với FPT được. Từng khoản chi ph&iacute; đều được SBA chắt chiu, c&acirc;n nhắc sử dụng.</span></span></span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:start\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"background-color:white\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\">&nbsp;</span></span></span></span></p>\r\n\r\n<p style=\"text-align:start\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"background-color:white\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"font-size:9pt\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\">Mặt kh&aacute;c, ĐHĐCĐ năm nay sẽ tiến h&agrave;nh bầu Hội đồng quản trị, Ban kiểm so&aacute;t nhiệm kỳ mới, đ&ograve;i hỏi phải thảo luận nhiều nội dung quan trọng n&ecirc;n cần họp trực tiếp.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:start\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"background-color:white\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\">&nbsp;</span></span></span></span></p>\r\n\r\n<p style=\"text-align:start\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"background-color:white\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"font-size:9pt\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\">Nhằm đảm bảo&nbsp;an to&agrave;n sức khỏe, lợi &iacute;ch cho cổ đ&ocirc;ng trước t&igrave;nh h&igrave;nh dịch bệnh v&agrave; thực hiện tu&acirc;n thủ đ&uacute;ng quy định của ph&aacute;p luật v&agrave; kinh tế, SBA đ&atilde; đề xuất thay đổi lịch họp ĐHĐCĐ năm 2020 v&agrave; đ&atilde; được c&aacute;c cơ quan chức năng chấp thuận, dự kiến ĐHĐCĐ năm 2020 sẽ tổ chức v&agrave;o ng&agrave;y 30/6/2020.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:start\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"background-color:white\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\">&nbsp;</span></span></span></span></p>\r\n\r\n<p style=\"text-align:start\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"background-color:white\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><strong><span style=\"font-size:9pt\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\">Tr&acirc;n trọng.</span></span></strong></span></span></span></span></p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:989px; top:749px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/icon/16.png\" /></div>', 'Ban Tổ chức ĐHĐCĐ năm 2020', NULL, '2020-04-26 12:24:05', '2020-04-26 12:24:05'),
(2, 1, 2, '<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"background-color:#ffffff\"><strong>K&iacute;nh gửi &Ocirc;ng Phạm Minh Ngọc</strong></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"background-color:#ffffff\">&nbsp;&nbsp;&nbsp;Đầu ti&ecirc;n xin cảm ơn &Ocirc;ng đ&atilde; quan t&acirc;m theo d&otilde;i t&igrave;nh h&igrave;nh hoạt động sản xuất kinh doanh của C&ocirc;ng ty thời gian qua. Ch&uacute;ng t&ocirc;i đ&atilde; t&igrave;m t&ecirc;n &Ocirc;ng trong danh s&aacute;ch cổ đ&ocirc;ng C&ocirc;ng ty chốt đến ng&agrave;y 17/05/2019 nhưng kh&ocirc;ng c&oacute;, chứng tỏ &Ocirc;ng kh&ocirc;ng nghi&ecirc;m t&uacute;c. Tuy nhi&ecirc;n, về c&acirc;u hỏi của &Ocirc;ng, ch&uacute;ng t&ocirc;i trả lời như sau:</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:38px; text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"background-color:#ffffff\"><strong><span style=\"font-size:9pt\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\">1.&nbsp;&nbsp;&nbsp;</span></span></strong><strong>&nbsp;&nbsp;Về vận h&agrave;nh NMTĐ Khe Di&ecirc;n th&aacute;ng 11:</strong></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"background-color:#ffffff\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Năm 2019 l&agrave; năm kiệt nhất kể từ khi vận h&agrave;nh (năm 2007) đến nay của nh&agrave; m&aacute;y. Cuối th&aacute;ng 10, đầu th&aacute;ng 11 tr&ecirc;n lưu vực hồ c&oacute; mưa, lưu lượng nước về c&oacute; cải thiện, nhưng rất &iacute;t, mực nước hồ đến ng&agrave;y 30/11 chỉ 203,2m&nbsp;(mới đạt 50% dung t&iacute;ch hữu &iacute;ch của hồ).</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"background-color:#ffffff\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Trước t&igrave;nh h&igrave;nh đ&oacute;, Nh&agrave; m&aacute;y chỉ vận h&agrave;nh giờ cao điểm theo Biểu gi&aacute; chi ph&iacute; tr&aacute;nh được để t&iacute;ch nước ph&aacute;t điện đủ giờ cao điểm m&ugrave;a kh&ocirc; năm 2020.</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:38px; text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"background-color:#ffffff\"><strong><span style=\"font-size:9pt\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\">2.&nbsp;&nbsp;&nbsp;</span></span></strong><strong>&nbsp;&nbsp;Về vận h&agrave;nh xả lũ NMTĐ Kr&ocirc;ng H&rsquo;năng:</strong></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"background-color:#ffffff\">Trong qu&aacute; tr&igrave;nh quản l&yacute; vận h&agrave;nh nh&agrave; m&aacute;y, SBA thường xuy&ecirc;n theo d&otilde;i th&ocirc;ng tin thời tiết tr&ecirc;n c&aacute;c Website http://www.kttv.gov.vn,&nbsp;<a href=\"http://www.vnbaolut.com/\" style=\"font-size:9pt; font-family:Tahoma, Arial, Helvetica, sans-serif\"><span style=\"font-size:9pt\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\">http://www.vnbaolut.com</span></span></a>,&nbsp;<a href=\"https://www.windy.com/\" style=\"font-size:9pt; font-family:Tahoma, Arial, Helvetica, sans-serif\"><span style=\"font-size:9pt\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\">https://www.windy.com</span></span></a>&nbsp;để nắm kịp thời t&igrave;nh h&igrave;nh thời tiết, nghi&ecirc;n cứu c&aacute;c giải ph&aacute;p vận h&agrave;nh xả lũ hợp l&yacute; để sẵn s&agrave;ng ứng ph&oacute; khi c&oacute; mưa, lũ xảy ra. Nhờ vậy, việc vận h&agrave;nh nh&agrave; m&aacute;y lu&ocirc;n chủ động, hiệu quả, cụ thể:</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"background-color:#ffffff\">&nbsp;&nbsp;&nbsp;&nbsp;Từ th&aacute;ng 10, qua nghi&ecirc;n cứu nắm bắt th&ocirc;ng tin về thời tiết, SBA dự liệu th&aacute;ng 11 sẽ xuất hiện lũ n&ecirc;n nh&agrave; m&aacute;y đ&atilde; chủ động vận h&agrave;nh ph&aacute;t điện, khai th&aacute;c hết nước trong hồ để sẵn s&agrave;ng đ&oacute;n lũ, mực nước hồ từ th&aacute;ng 10 đến đầu th&aacute;ng 11 c&oacute; l&uacute;c dưới mực nước chết.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"background-color:#ffffff\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Như dự liệu, đến 00h00&rsquo; ng&agrave;y 10/11, từ ảnh hưởng của Cơn b&atilde;o số 06, tr&ecirc;n lưu vực hồ thủy điện Kr&ocirc;ng H&rsquo;năng bắt đầu c&oacute; mưa, lưu lượng nước về hồ đ&atilde; tăng từ 18 m<sup>3</sup>/s l&ecirc;n 37 m<sup>3</sup>/s. SBA đ&atilde; chuyển ngay sang chế độ trực ứng ph&oacute; lụt, b&atilde;o 24/24 giờ, c&ocirc;ng t&aacute;c quan trắc lượng mưa, cập nhật số liệu mực nước hồ, t&iacute;nh to&aacute;n lưu lượng nước về hồ được cập nhật ở chế độ tần suất 15 ph&uacute;t/ lần.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"background-color:#ffffff\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Đến 22h00&rsquo; ng&agrave;y 10/11, lượng mưa tr&ecirc;n lưu vực hồ tiếp tục tăng tr&ecirc;n diện rộng, tổng lượng mưa b&igrave;nh qu&acirc;n lưu vực đo được l&agrave; 176,2 mm/13 giờ, lưu lượng nước về hồ từ 37 m<sup>3</sup>/s đ&atilde; tăng nhanh l&ecirc;n 1.772 m<sup>3</sup>/s, mực nước hồ đạt 246,77 m&nbsp;(c&ograve;n thấp hơn 4,73 m so với mực nước đ&oacute;n lũ +251,5m theo Quy định).</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"background-color:#ffffff\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Từ 22h00&rsquo; ng&agrave;y 10/11 đến 02h00&rsquo; ng&agrave;y 11/11: Cường độ mưa c&oacute; giảm, tổng lượng mưa b&igrave;nh qu&acirc;n lưu vực đo được l&agrave; 40,7 mm/4 giờ; lưu lượng nước về hồ tăng chậm từ 1.772 m<sup>3</sup>/s đến đạt đỉnh l&agrave; 1.928 m<sup>3</sup>/s (l&uacute;c 01h15).</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"background-color:#ffffff\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Để tạo dung t&iacute;ch trống cắt đỉnh lũ, giảm xả lũ về hạ du. Nh&agrave; m&aacute;y đ&atilde; vận h&agrave;nh điều tiết xả nước qua tr&agrave;n từ l&uacute;c 02h00&rsquo; ng&agrave;y 11/11 với lưu lượng ban đầu l&agrave; 5 m<sup>3</sup>/s v&agrave; tăng dần đến 1.120 m<sup>3</sup>/s (mức xả tr&agrave;n lớn nhất). Tuy nhi&ecirc;n khi b&atilde;o v&agrave;o đến đất liền khu vực Ph&uacute; Y&ecirc;n th&igrave; nhanh ch&oacute;ng tan n&ecirc;n C&ocirc;ng ty chủ động giảm lượng xả để t&iacute;ch nước cho m&ugrave;a cạn năm 2020.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"background-color:#ffffff\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Đến 08h00&rsquo; ng&agrave;y 15/11: To&agrave;n lưu vực kh&ocirc;ng c&ograve;n mưa, lượng nước về hồ thấp&nbsp;hơn lưu lượng ph&aacute;t điện, Nh&agrave; m&aacute;y đ&atilde; vận h&agrave;nh đ&oacute;ng ho&agrave;n to&agrave;n c&aacute;c cửa van, kết th&uacute;c xả tr&agrave;n với mực nước hồ đạt MNDBT +255,00 m.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"background-color:#ffffff\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Từ khi xuất hiện lũ 10/11, nh&agrave; m&aacute;y đ&atilde; vận h&agrave;nh li&ecirc;n tục ph&aacute;t c&ocirc;ng suất tối đa để khai th&aacute;c lượng nước về. Nhờ quan trắc tốt lưu lượng nước về hồ n&ecirc;n đ&atilde; chủ động vận h&agrave;nh giảm lưu lượng xả tr&agrave;n, cắt được 42% đỉnh lũ&nbsp;(1.928 m<sup>3</sup>/s)&nbsp;để t&iacute;ch nước v&agrave;o hồ ph&aacute;t điện, cuối đợt lũ hồ đầy nước. Nhờ đ&oacute; m&agrave; đến ng&agrave;y 30/11, C&ocirc;ng ty đ&atilde; đạt kế hoạch sản lượng năm:&nbsp;190,0/190,0 triệu kWh, doanh thu ph&aacute;t điện đạt 234,18/214,76 tỷ đồng, đạt 109% kế hoạch năm.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"background-color:#ffffff\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Về phản &aacute;nh&ldquo;..vận h&agrave;nh của NMTĐ Kr&ocirc;ng H&rsquo;năng thật ph&iacute; t&agrave;i nguy&ecirc;n nước.&rdquo;&nbsp;của &Ocirc;ng với SBA l&agrave; kh&ocirc;ng hiểu g&igrave; về việc nghi&ecirc;n cứu vận h&agrave;nh lũ của SBA. SBA đ&atilde; c&oacute; nhiều kết quả được khen thưởng về việc n&agrave;y như chế tạo thiết bị đo mực nước hồ đạt ch&iacute;nh x&aacute;c đến 1 milim&eacute;t, đo mưa tự động, vận h&agrave;nh xả lũ tự động.v.v n&ecirc;n việc vận h&agrave;nh điều tiết lũ h&agrave;ng năm lu&ocirc;n được UBND tỉnh Ph&uacute; Y&ecirc;n tặng bằng khen, được Cục KTAT&amp;MT thuộc Bộ C&ocirc;ng Thương đ&aacute;nh gi&aacute; cao v&agrave; mời b&aacute;o c&aacute;o điển h&igrave;nh.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"background-color:#ffffff\">&nbsp;&nbsp;&nbsp;&nbsp;Dung t&iacute;ch hữu &iacute;ch của hồ Kr&ocirc;ng H&rsquo;năng chỉ chứa được 108 triệu m<sup>3</sup>, lượng nước về đợt lũ vừa rồi l&agrave; 153,21 triệu m<sup>3</sup>&nbsp;m&agrave; SBA chỉ xả tr&agrave;n 21,86 triệu m<sup>3</sup>&nbsp;đ&atilde; cho thấy việc vận h&agrave;nh hiệu quả của SBA. C&aacute;c hồ thủy điện kh&aacute;c kh&ocirc;ng c&oacute; mưa th&igrave; kh&ocirc;ng c&oacute; nước, SBA kh&ocirc;ng mang nước đến cho hồ kh&aacute;c được, mong &Ocirc;ng hiểu cho!&nbsp;&nbsp;&nbsp;&nbsp;</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:38px; text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"background-color:#ffffff\"><strong><span style=\"font-size:9pt\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\">3.&nbsp;&nbsp;&nbsp;</span></span></strong><strong>&nbsp;&nbsp;Kế hoạch SXKD năm 2020:</strong></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"background-color:#ffffff\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Tr&ecirc;n cơ sở dự b&aacute;o của Trung t&acirc;m Dự b&aacute;o KTTV Quốc gia, c&aacute;c m&ocirc; h&igrave;nh C&ocirc;ng ty nghi&ecirc;n cứu từ dự b&aacute;o về nhiệt độ sai chuẩn Th&aacute;i B&igrave;nh Dương v&agrave; ảnh hưởng c&aacute;c chỉ số &aacute;p suất kh&iacute; quyển T&acirc;y Th&aacute;i B&igrave;nh Dương (SOI) v&agrave; nhiệt độ nước biển lưỡng cực Ấn Độ Dương (SOD) của lưu lượng về hồ Kr&ocirc;ng H&rsquo;năng, Khe Di&ecirc;n, SBA dự b&aacute;o năm 2020 t&igrave;nh h&igrave;nh thủy văn sẽ c&ograve;n nhiều kh&oacute; khăn.</span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"background-color:#ffffff\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;C&ocirc;ng ty dự kiến tr&igrave;nh ĐHĐCĐ kế hoạch SXKD năm 2020 như sau: Tổng sản lượng 02 nh&agrave; m&aacute;y 197 triệu kWh, tương ứng doanh thu l&agrave;&nbsp;<span style=\"font-size:9pt\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\">232,67 tỷ đồng.</span></span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></span></span></span></p>\r\n\r\n<p style=\"margin-left:38px; text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"background-color:#ffffff\"><strong><span style=\"font-size:9pt\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\">4.&nbsp;&nbsp;&nbsp;</span></span></strong><strong>&nbsp;&nbsp;Cổ tức năm 2019:</strong></span></span></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:12px\"><span style=\"font-family:Tahoma,Arial,Helvetica,sans-serif\"><span style=\"color:#000000\"><span style=\"background-color:#ffffff\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Với kết quả vận h&agrave;nh tốt của NMTĐ Kr&ocirc;ng H&rsquo;năng, năm 2019 SBA sẽ vượt kế hoạch SXKD đề ra, l&atilde;i cơ bản tr&ecirc;n cổ phiếu dự kiến tr&ecirc;n 1.500 đồng/CP. Về cổ tức 